mapper.update:
UPDATE  board
SET     title = #{title}
,       contents_body = #{contentsBody}
,       update_user = #{updateUser}
,       update_dt = now()
WHERE   id = #{id}

UPDATE  board
SET     use_yn = 'N'
WHERE   id = #{id}

mapper.select:
SELECT id , title , contents_body AS contentsBody , use_yn AS useYn , create_dt AS createDateTime , create_user AS createUser , update_dt AS updateDateTime , update_user AS updateUser FROM board WHERE use_yn = 'Y'
AND id = #{id}
ORDER BY create_dt DESC
